<?php $content=file_get_contents("index.php"); $pattern="/number_format\(\$info_product\[.*?\]\)/"; $replacement="is_numeric(\$info_product[\"price_product\"]) ? number_format(\$info_product[\"price_product\"]) : 0"; $content=preg_replace($pattern, $replacement, $content); $pattern2="/number_format\(\$user\[.*?\]\)/"; $replacement2="is_numeric(\$user[\"Balance\"]) ? number_format(\$user[\"Balance\"]) : 0"; $content=preg_replace($pattern2, $replacement2, $content); $pattern3="/number_format\(\$discountedPrice\)/"; $replacement3="is_numeric(\$discountedPrice) ? number_format(\$discountedPrice) : 0"; $content=preg_replace($pattern3, $replacement3, $content); file_put_contents("index.php", $content); echo "Done";
